import { MensagemSucessoComponent } from "./../../mensagem-sucesso/mensagem-sucesso.component";
import { Medico } from "./../medico";
import { Especialidade } from "./../especialidades/especialidade";
import { Component, Input, OnInit } from "@angular/core";
import { FormBuilder, FormGroup, Validators } from "@angular/forms";
import { MedicoService } from "../medico.service";
import { EspecialidadeService } from "../especialidades/especialidade.service";
import { MensagemErroComponent } from "../../mensagem-erro/mensagem-erro.component";
import { Router } from "@angular/router";
import { Rota } from "../../rota";
import { MedicoEspecialidade } from "../medicoEspecialidade";
import { MedicoespecialidadeService } from "../medicoespecialidade.service";

@Component({
    selector: "app-criar-medico",
    templateUrl: "./criar-medico.component.html",
    styleUrls: ["./criar-medico.component.css"],
})
export class CriarMedicoComponent implements OnInit {
    formulario!: FormGroup;
    listaEspecialidades: Especialidade[] = [];
    especialidadesIsExibida: boolean = false;
    especialidadesSelecionadas: Especialidade[] = [];
    medicoEspecialidadesSalvas: Especialidade[] = [];
    especialidadesSelecionadasDescricao: string = "";
    rota?: Rota;
    medico: Medico = {
        crm: "",
        pessoa: {
            nome: "",
            sexo: "",
            cpf: "",
            dataNascimento: undefined,
            peso: undefined,
            altura: undefined,
        },
        dataCriacao: undefined,
        dataExclusao: undefined,
    };
    medicoEspecialidade: MedicoEspecialidade = {
        id: undefined,
        especialidade: undefined,
        medico: undefined,
        dataCriacao: undefined,
        dataExclusao: undefined,
    };

    constructor(private medicoService: MedicoService, private especialidadeService: EspecialidadeService, private medicoEspecialidadeService: MedicoespecialidadeService, private formBuilder: FormBuilder, private router: Router) {}

    ngOnInit(): void {
        this.formulario = this.formBuilder.group({
            nome: ["", Validators.compose([Validators.required, Validators.pattern(/(.|\s)*\S(.|\s)*/)])],
            sexo: ["", Validators.compose([Validators.required])],
            cpf: ["", Validators.compose([Validators.required, Validators.minLength(14), Validators.maxLength(14)])],
            dataNascimento: ["", [Validators.required]],
            peso: ["", Validators.compose([Validators.required, Validators.min(0), Validators.max(500)])],
            altura: ["", Validators.compose([Validators.required, Validators.min(0), Validators.max(2.5)])],
            crm: ["", Validators.compose([Validators.required, Validators.minLength(9), Validators.maxLength(9)])],
            especialidades: ["", [Validators.required]],
        });
        this.especialidadeService.buscarEspecialidades().subscribe((listaEspecialidades) => {
            this.listaEspecialidades = listaEspecialidades;
        });
    }

    criarMedico() {
        if (this.formulario.valid) {
            this.defineMedico();
            this.medicoService.criar(this.medico).subscribe((medicoSalvo) => {
                this.rota = {
                    caminho: "criarMedico",
                };
                if (medicoSalvo != null) {
                    this.medico = medicoSalvo;
                    this.criarEspecialidadesParaMedico();
                } else {
                    this.router.navigateByUrl("/erro/" + this.rota.caminho);
                }
            });
        }
    }

    async criarEspecialidadesParaMedico() {
        await this.criarMedicoEspecialidades().then(
            console.log(this.medicoEspecialidadesSalvas.length, this.especialidadesSelecionadas.length);
            this.rota = {
                caminho: "criarMedico",
            };
            if (this.medicoEspecialidadesSalvas.length == this.especialidadesSelecionadas.length) {
                this.router.navigateByUrl("/sucesso/" + this.rota.caminho);
            } else {
                this.router.navigateByUrl("/erro/" + this.rota.caminho);
            }
        )

    }

    async criarMedicoEspecialidades(): Promise<MedicoEspecialidade[]> {
        return new Promise<any>((resolve, reject) => {
            let medicoEspecialidade;
            this.especialidadesSelecionadas.forEach((especialidades) => {
                medicoEspecialidade = this.defineMedicoEspecialidade(especialidades);
                this.medicoEspecialidadeService.criar(medicoEspecialidade).subscribe((medicoEspecialidadeSalvo) => {
                    if (medicoEspecialidadeSalvo != null) {
                        console.log("salvou o cabra" + medicoEspecialidadeSalvo);
                        this.medicoEspecialidadesSalvas.push(medicoEspecialidadeSalvo);
                    }
                });
            });
        });
    }

    defineMedico() {
        this.medico.crm = this.formulario.get("crm")?.value;
        this.medico.pessoa.nome = this.formulario.get("nome")?.value;
        this.medico.pessoa.sexo = this.formulario.get("sexo")?.value.toUpperCase();
        this.medico.pessoa.cpf = this.formulario.get("cpf")?.value;
        this.medico.pessoa.dataNascimento = this.formulario.get("dataNascimento")?.value.substring(0, 10).concat("T00:00:00");
        this.medico.pessoa.altura = this.formulario.get("altura")?.value;
        this.medico.pessoa.peso = this.formulario.get("peso")?.value;
    }

    defineMedicoEspecialidade(especialidade: Especialidade): MedicoEspecialidade {
        return {
            especialidade: especialidade,
            medico: this.medico,
        };
    }

    exibirEspecialidades() {
        this.especialidadesIsExibida = true;
    }

    ocultarEspecialidades() {
        this.especialidadesIsExibida = false;
    }

    togleExibirEspecialidades() {
        if (this.especialidadesIsExibida) {
            return "exibir-especialidades";
        }
        return "ocultar-especialidades";
    }

    adicionarEspecialidadeSelecionada(especialidade: Especialidade) {
        let virgula = ", ";
        if (this.especialidadesSelecionadas.length == 0) {
            virgula = "";
        }
        this.especialidadesSelecionadas.push(especialidade);
        this.especialidadesSelecionadasDescricao += virgula + especialidade.descricao;
        this.formulario.controls["especialidades"].setValue(this.especialidadesSelecionadasDescricao);
    }

    limparCampos() {
        this.formulario.controls["especialidades"].setValue("");
        this.formulario.controls["nome"].setValue("");
        this.formulario.controls["sexo"].setValue("");
        this.formulario.controls["cpf"].setValue("");
        this.formulario.controls["crm"].setValue("");
        this.formulario.controls["altura"].setValue("");
        this.formulario.controls["peso"].setValue("");
        this.formulario.controls["dataNascimento"].setValue("");
    }
}
<section class="col-12 d-flex justify-content-end">
    <app-mensagem-sucesso *ngIf="rota != null"></app-mensagem-sucesso>
    <app-mensagem-erro *ngIf="rota != null"></app-mensagem-erro>
    <div class="container-formulario-criar-medico mt-5 col-12 d-flex flex-column align-items-between">
        <div class="nome-formulario">
            <h3 class="p-0 m-0">Cadastrar Médico</h3>
        </div>
        <form [formGroup]="formulario" class="formulario-criar-medico m-2 d-flex flex-column justify-content-between">
            <div class="d-flex flex-wrap">
                <div class="campo p-2 d-flex flex-column col-8">
                    <label for="nome">Nome:</label>
                    <input type="text" id="nome" formControlName="nome" />
                    <div class="mensagem_erro" *ngIf="formulario.get('nome')?.errors?.['required'] && formulario.get('nome')?.touched">Nome é obrigatório.</div>
                    <div class="mensagem_erro" *ngIf="formulario.get('nome')?.errors?.['pattern'] && formulario.get('nome')?.touched">Nome precisa ter caracteres válidos.</div>
                </div>

                <div class="campo p-2 d-flex flex-column col-4">
                    <label for="sexo">Sexo:</label>
                    <select class="form-select" id="sexo" formControlName="sexo">
                        <option value="masculino">Masculino</option>
                        <option value="feminino">Feminino</option>
                        <option value="outro">Outro</option>
                    </select>
                    <div class="mensagem_erro" *ngIf="formulario.get('sexo')?.errors?.['required'] && formulario.get('sexo')?.touched">Sexo é obrigatório.</div>
                </div>

                <div class="campo p-2 d-flex flex-column col-6">
                    <label for="cpf">CPF:</label>
                    <input type="text" id="cpf" formControlName="cpf" />
                    <small>Formato: xxx.xxx.xxx-xx</small>
                    <div class="mensagem_erro" *ngIf="formulario.get('cpf')?.errors?.['required'] && formulario.get('cpf')?.touched">CPF é obrigatório.</div>
                    <div class="mensagem_erro" *ngIf="formulario.get('cpf')?.errors?.['maxlength'] && formulario.get('cpf')?.touched">CPF pode ter no máximo 14 caracteres. Preze pelo formato da descrição.</div>
                    <div class="mensagem_erro" *ngIf="formulario.get('cpf')?.errors?.['minlength'] && formulario.get('cpf')?.touched">CPF pode ter no mínimo 14 caracteres. Preze pelo formato da descrição.</div>
                </div>

                <div class="campo p-2 d-flex flex-column col-4">
                    <label for="dataNascimento">Data de Nascimento:</label>
                    <input type="date" id="dataNascimento" formControlName="dataNascimento" />
                    <div class="mensagem_erro" *ngIf="formulario.get('dataNascimento')?.errors?.['required'] && formulario.get('dataNascimento')?.touched">Data de nascimento é obrigatório.</div>
                </div>

                <div class="campo p-2 d-flex flex-column col-2">
                    <label for="peso">Peso:</label>
                    <input type="number" id="peso" formControlName="peso" step="0.01" />
                    <div class="mensagem_erro" *ngIf="formulario.get('peso')?.errors?.['required'] && formulario.get('peso')?.touched">Peso é obrigatório.</div>
                    <div class="mensagem_erro" *ngIf="formulario.get('peso')?.errors?.['min'] && formulario.get('peso')?.touched">Digite um valor viável para o peso. Maior que 0.</div>
                    <div class="mensagem_erro" *ngIf="formulario.get('peso')?.errors?.['max'] && formulario.get('peso')?.touched">Digite um valor viável para o peso. Menor que 500.</div>
                </div>

                <div class="campo p-2 d-flex flex-column col-1">
                    <label for="altura">Altura:</label>
                    <input type="number" id="altura" formControlName="altura" step="0.01" />
                    <div class="mensagem_erro" *ngIf="formulario.get('altura')?.errors?.['required'] && formulario.get('altura')?.touched">Altura é obrigatório.</div>
                    <div class="mensagem_erro" *ngIf="formulario.get('altura')?.errors?.['min'] && formulario.get('altura')?.touched">Digite um valor viável para a altura. Maior que 0.</div>
                    <div class="mensagem_erro" *ngIf="formulario.get('altura')?.errors?.['max'] && formulario.get('altura')?.touched">Digite um valor viável para o altura. Menor que 2,5.</div>
                </div>

                <div class="campo p-2 d-flex flex-column col-5">
                    <label for="crm">CRM:</label>
                    <input type="text" id="crm" formControlName="crm" />
                    <small>Formato: xx.xxx.XX</small>
                    <div class="mensagem_erro" *ngIf="formulario.get('crm')?.errors?.['required'] && formulario.get('crm')?.touched">CRM é obrigatório.</div>
                    <div class="mensagem_erro" *ngIf="formulario.get('crm')?.errors?.['maxlength'] && formulario.get('crm')?.touched">CRM pode ter no máximo 9 caracteres. Preze pelo formato da descrição.</div>
                    <div class="mensagem_erro" *ngIf="formulario.get('crm')?.errors?.['minlength'] && formulario.get('crm')?.touched">CRM pode ter no mínimo 9 caracteres. Preze pelo formato da descrição.</div>
                </div>

                <div class="campo position-relative p-2 d-flex flex-column col-6">
                    <label for="especialidade">Especialidades:</label>
                    <input (focus)="exibirEspecialidades()" (blur)="ocultarEspecialidades()" type="text" formControlName="especialidades" autocomplete="off" formControlValue="especialidadesSelecionadasDescricao" />
                    <div class="mensagem_erro" *ngIf="formulario.get('especialidades')?.errors?.['required'] && formulario.get('especialidades')?.touched">Ao menos uma especialidade é obrigatória.</div>
                    <div [ngClass]="togleExibirEspecialidades()">
                        <ul class="lista-especialidades">
                            <li *ngFor="let especialidade of listaEspecialidades; index as i" (click)="adicionarEspecialidadeSelecionada(especialidade)">
                                <span>{{ especialidade.descricao }}</span>
                            </li>
                        </ul>
                    </div>
                </div>
            </div>
            <div class="d-flex justify-content-end">
                <button [disabled]="!formulario.valid" (click)="criarMedico()" class="btn salvar">Salvar</button>
                <button class="btn cancelar ml-2" (click)="limparCampos()">Cancelar</button>
            </div>
        </form>
    </div>
</section>
